package state

import (
	"testing"

	"github.com/blocto/solana-go-sdk/common"
	"github.com/stretchr/testify/require"
)

const positionData = ""

func TestDeserializePositionData(t *testing.T) {
	data, err := DeserializeData[Position](positionData)
	require.NoError(t, err)

	pb := common.PublicKeyFromString("AM2MgW3Vg6GjEoPhH7wUz8r7YAewstxu7SRQ6B6qkpsg")
	require.Equal(t, pb, data.Owner)
}
